// Wishlist.js
import React, { useState } from 'react';
import axios from 'axios';

const WishListComponent = () => {
    const [bookTitle, setBookTitle] = useState('');
    const [bookAuthor, setBookAuthor] = useState('');
    const [publishedYear, setPublishedYear] = useState('');
    const [genre, setGenre] = useState('');

    const handleAddToWishlist = () => {
        axios.post('/api/books', { bookTitle, bookAuthor,publishedYear,genre })
            .then(response => console.log('Book added to wishlist:', response.data))
            .catch(error => console.error('Error adding book to wishlist:', error));
    };

    return (
        <div>
            <h2>Wishlist</h2>
            <div>
                <label>Book Title: </label>
                <input type="text" value={bookTitle} onChange={e => setBookTitle(e.target.value)} />
            </div>

            <div>
                <label>Book Author: </label>
                <input type="text" value={bookAuthor} onChange={e => setBookAuthor(e.target.value)} />
            </div>

             <div>
                <label>Published Year: </label>
                <input type="text" value={publishedYear} onChange={e => setPublishedYear(e.target.value)} />
            </div>

             <div>
                <label>Genre: </label>
                <input type="text" value={genre} onChange={e => setGenre(e.target.value)} />
            </div>

            <button onClick={handleAddToWishlist}>Add to Wishlist</button>
        </div>
    );
};

export default WishListComponent;
