import React, { Component } from 'react';

class ViewBookList extends Component {
    constructor(props){
        super(props)

        this.state = {
           // bookId: this.props.match ? this.props.match.params.bookId : '',
            //books: {}
            };
    }
     componentDidMount() {
        // 
        fetch(`http://localhost:8080/api/books${this.state.bookId}`)
            .then(response => response.json())
            .then(data => {
                this.setState({ books: data });
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    render() {
    const { books } = this.state;
    return (
        <div>
            <div className='card col-md-6 offset-md-3'>
                <h3 className='text-center'>View BookList Page</h3>
                <div className='card-body'>
                    {books ? (
                        <div>
                            {books.map((book, index) => (
                                <div key={index}>
                                    <div className='row'>
                                        <label>Book Title: </label>
                                        <div>{book.bookTitle}</div>
                                    </div>

                                    <div className='row'>
                                        <label>Book Author: </label>
                                        <div>{book.bookAuthor}</div>
                                    </div>

                                    <div className='row'>
                                        <label>Published Year: </label>
                                        <div>{book.publishedYear}</div>
                                    </div>

                                    <div className='row'>
                                        <label>Genre: </label>
                                        <div>{book.genre}</div>
                                    </div>
                                    <div className='text-center'>{book.isAvailable ? 'Available to borrow' : 'Not Available'}</div>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div>No books available</div>
                    )}
                </div>
            </div>
        </div>
    );
}
}
export default ViewBookList;
