import React, { useState, useEffect } from 'react';

const ViewBookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Function to fetch book data from the API
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/books');
        const data = await response.json();
        setBooks(data);
      } catch (error) {
        console.error('Error fetching book data:', error);
      }
    };

    // Call the fetchBooks function when the component mounts
    fetchBooks();
  }, []); // Empty dependency array ensures the effect runs only once on mount

  return (
    <div>
        <div className='card col-md-6 offset-md-3'>
        <h3 className='text-center'>Book Catalog</h3>
      <ul>
        {books.map((book) => (
          <li key={book.bookId}>
            <h3 className="label">Book Title:</h3><p>{book.bookTitle}</p><br></br>
            <h3 className="label">Book Author: <p>{book.bookAuthor}</p> </h3><br></br>
            <span className="label">Published Year: {book.publishedYear} </span><br></br>
            <span className="label">Genre: {book.genre} </span><br></br>
            
            <button disabled={!book.isAvailable}>Borrow</button>
          </li>
        ))}
      </ul>
    </div>
    </div>
  );
};

export default ViewBookList;
