import React, { Component } from 'react';
import BookListServiceFetch from '../services/BookListServiceFetch';

class AddBookListComponent extends Component {
    constructor(props) {
        super(props);

        this.state = {
            bookTitle: '',
            bookAuthor: '',
            publishedYear: '',
            genre: '',
        };

        // Binding
        this.changeBookTitleHandler = this.changeBookTitleHandler.bind(this);
        this.changeBookAuthorHandler = this.changeBookAuthorHandler.bind(this);
        this.changepublishedYearHandler = this.changepublishedYearHandler.bind(this);
        this.changegenreHandler = this.changegenreHandler.bind(this);
        this.saveBook = this.saveBook.bind(this);
    }
   // Create event handler for adding a book
    saveBook = (event) => {
        event.preventDefault();
        let bookList = {
            bookTitle: this.state.bookTitle,
            bookAuthor: this.state.bookAuthor,
            publishedYear: this.state.publishedYear,
            genre: this.state.genre,
        };

        console.log('bookList => ' + JSON.stringify(bookList));

        // Call service
        BookListServiceFetch.addBookListComponent(bookList).then((response) => {
            this.props.history.push('/addBookList');
        });
    };

    changeBookTitleHandler = (event) => {
        this.setState({ bookTitle: event.target.value });
    };

    changeBookAuthorHandler = (event) => {
        this.setState({ bookAuthor: event.target.value });
    };

    changepublishedYearHandler = (event) => {
        this.setState({ publishedYear: event.target.value });
    };

    changegenreHandler = (event) => {
        this.setState({ genre: event.target.value });
    };

    // Cancel method to return to (navigate to) BookListComponent pages
    cancel() {
        this.props.history.push('/bookList');
    }


    render() {
        return (
            <div>
                <div className='container'>
                    <div className='row'>
                        <div className='card col-md-6 offset-md-3 offset-md-3'>
                            <h2 className='text-center'>Add your Book</h2>
                            <div className='card-body'>
                                <form>
                                    <div className='form-group'>
                                        <label>Book Title: </label>
                                        <input placeholder ="Book Title" name ='bookTitle' className ='force-control' 
                                        value= {this.state.bookTitle} onChange= {this.changeBookTitleHandler} />
                                    </div>

                                     <div className='form-group'>
                                        <label>Book Author: </label>
                                        <input placeholder ="Book Author" name ='bookAuthor' className ='force-control' 
                                        value= {this.state.bookAuthor} onChange= {this.changeBookAuthorHandler} />
                                    </div>

                                     <div className='form-group'>
                                        <label>Published Year: </label>
                                        <input placeholder ="Published Year" name ='publishedYear' className ='force-control' 
                                        value= {this.state.publishedYear} onChange= {this.changepublishedYearHandler} />
                                    </div>

                                    <div className='form-group'>
                                        <label>Genre: </label>
                                        <input placeholder ="Genre" name ='genre' className ='force-control' 
                                        value= {this.state.genre} onChange= {this.changegenreHandler} />
                                    </div>

                                    <button className='btn btn-success' onClick={this.saveBook}>Add</button>
                                    <button className='btn btn-danger' onClick={this.cancel.bind(this)} style={{marginLeft:"10px"}}>Cancel</button>
                                   
                                </form>
                            </div>
                             
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AddBookListComponent;
