import React from 'react';
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Swal from "sweetalert2";

function Editbookcategory() {
    const { cid } = useParams();
    const[validation,valchange]=useState(false);
    const[id,idchange]=useState("");
    const[name,namechange]=useState("");
 

    useEffect(() => {
       
        fetch("http://localhost:8080/bookCategories/" + cid).then((res) => {
            return res.json();
        }).then((resp) => {
            idchange(resp.id);
            namechange(resp.name);
                       
        }).catch((err) => {
            console.log(err.message);
        })
    }, []);

    
    


    const navigate=useNavigate();

    const handlesubmit=(e)=>{
      e.preventDefault();
      const data={id,name};
      

      fetch("http://localhost:8080/bookCategories/"+cid,{
        method:"PUT",
        headers:{"content-type":"application/json"},
        body:JSON.stringify(data)
      }).then((res)=>{
        //alert('Saved successfully.')
        Swal.fire({icon: "success",
        title: "Updated successfully."});
        navigate('/listbookcategory');
      }).catch((err)=>{
        console.log(err.message)
      })

    }
    return ( 
        <div>

        <div className="row">
            <div className="offset-lg-3 col-lg-6">
                <form className="container" onSubmit={handlesubmit}>

                    <div className="card" style={{"textAlign":"left"}}>
                        <div className="card-title">
                            <h2>booksCategory Edit</h2>
                        </div>
                        <div className="card-body">

                            <div className="row">

                                <div className="col-lg-12">
                                    <div className="form-group">
                                        <label>ID</label>
                                        <input value={id} disabled="disabled" className="form-control"></input>
                                    </div>
                                </div>

                                <div className="col-lg-12">
                                    <div className="form-group">
                                        <label>Name</label>
                                        <input required value={name} onMouseDown={e=>valchange(true)} onChange={e=>namechange(e.target.value)} className="form-control"></input>
                                    {name.length===0 && validation && <span className="text-danger">Enter the name</span>}
                                    </div>
                                </div>

                                
                                <div className="col-lg-12">
                                    <div className="form-group">
                                       <button className="btn btn-success" type="submit">Save</button>
                                       <Link to="/listbookcategory" className="btn btn-danger">Back</Link>
                                    </div>
                                </div>

                            </div>

                        </div>

                    </div>

                </form>

            </div>
        </div>
    </div>
     );
}
 
 

export default Editbookcategory;
