import React, { useState, useEffect } from 'react';

const WishListComponent = () => {
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    // Fetch wishlist data from the Spring Boot backend
    fetch('http://localhost:8080/api/wishlist')
      .then(response => response.json())
      .then(data => setWishlist(data))
      .catch(error => console.error('Error fetching wishlist:', error));
  }, []); // Empty dependency array ensures the effect runs once on component mount

  return (
    <div>
      <h2>Community Wishlist</h2>
       
      <form onSubmit={handleAddToWishlist}>
                <div>
                    <label>Book Title: </label>
                     <input type="text" value={bookTitle} onChange={event => setWishlist(event.target.value)} />
                </div>

               <div>
                     <label>Book Author: </label>
                    <input type="text" value={bookAuthor} onChange={event => setWishlist(event.target.value)} />
                </div>

                <div>
                    <label>Published Year: </label>
                    <input type="text" value={publishedYear} onChange={event => setWishlist(event.target.value)} />
                </div>

                 <div>
                    <label>Genre: </label>
                    <input type="text" value={genre} onChange={event => setWishlist(event.target.value)} />
                </div>

                 <div>
                   <input type="submit" name="submit" value="Add to Wishlist" />
                 </div>

            
            </form>
        </div>
    )
 };

 export default WishListComponent;


