import React, { Component } from 'react';

class AddBookListComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            bookTitle: '',
            bookAuthor: '',
            publishedYear: '',
            genre: ''
        }
    }
    render() {
        return (
            <div>
                <div className='container'>
                    <div className='row'>
                        <div className='card col-md-6 offset-md-3 offset-md-3'>
                            <h3 className='text-center'>Add Book</h3>
                            <div className='card-body'>
                                <form>
                                    <div className='form-group'>
                                        <label>Book Title</label>
                                        <input placeholder="Book Title" name='bookTitle' className='force-control' 
                                        value={this.state.bookTitle} onChange={this.onChangeBookTitleHandler} />
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default AddBookListComponent;
